export const inflationInit = {
    "cpi_fed_explain" : {
        "explain1" : "長期的高通膨會引來嚴重的效應",
        "explain2" : "1. 直接相關的商品會率先漲價，連帶間接相關的商品漲價",
        "explain3" : "2. 通膨預期心理會導致，各行各業重新定價，第一波沒漲的會落後補漲",
        "explain4" : "3. 最後通膨不斷創新高",
        "explain5" : "當利率小於通膨，存入銀行的錢會減少，流通在市場上的錢會增加，這會加速通膨最後利率(FED) >= 通膨(CPI)",
    }
}
