import axios from 'axios';
import React, { useState } from 'react';
import { rootApiIP } from '../../constant';
import HighchartStockComp from '../highchart/highchatStockComp';

function CpiFedCard() {
    const [options, setOptions] = useState({
        yAxis: [{
            height: '80%',
        }, {
            top: '80%',
            height: '20%',
        }],
        xAxis: {
            type : "datetime",
        },
        chart : {
            height : 600
        },
        accessibility: {
            enabled : false
        },
        series: [],
        stockTools : {
            gui : {
                buttons : [ 'indicators', 'separator', 'simpleShapes', 'lines', 'crookedLines', 'measure', 'advanced',
                'toggleAnnotations', 'separator', 'verticalLabels', 'flags', 'separator', 'zoomChange', 'fullScreen',
                'typeChange', 'separator', 'currentPriceIndicator', 'saveChart' ]
            }
        },
        tooltip: {
            valueDecimals: 2
        },
        rangeSelector : {
            selected: 4
        },
        plotOptions : {
            series : {
                dataGrouping : {
                    enabled : false
                }
            }
        }
    });

    axios.post(rootApiIP + "/data/cpi_fed")
    .then((res) => {
        setOptions({
            series : [
                {
                    type : 'candlestick',
                    id : 'Kline',
                    name : 'Kline',
                    data : res.data["Kline"],
                },
                {
                    name : "support",
                    id: 'support',
                    data : res.data["support"]
                },
                {
                    name : "resistance",
                    id: 'resistance',
                    data : res.data["resistance"]
                },
                {
                    name : maLen + maType,
                    id: maLen + maType,
                    data : res.data["ma"]
                },
                {
                    type : "flags",
                    name: "Crossover",
                    data : res.data["annotations_labels"],
                    allowOverlapX : true
                },
                {
                    type: 'column',
                    name: 'Volume',
                    data: res.data["volume"],
                    yAxis: 1
                },
            ],
        })
    })

    return (
        <>
            <div className = 'card h-100'>
                <div className = "card-header text-center">
                    CPI (物價通貨膨脹指數) vs FED (聯邦基金利率)
                </div>

                <div className = 'card-body'>
                    <HighchartStockComp options = {options} />
                </div>
            </div>
        </>
    );
}

export default CpiFedCard;
