export const inflationInit = {
    "cpi_fed_explain" : {
        "explain0" : "通膨定義 6% > CPI >= 3%，高通膨定義 CPI >= 6%",
        "explain1" : "長期的高通膨會引來嚴重的效應",
        "explain2" : "1. 直接相關的商品會率先漲價，連帶間接相關的商品漲價",
        "explain3" : "2. 通膨預期心理會導致，各行各業重新定價，第一波沒漲的會落後補漲",
        "explain4" : "3. 最後通膨不斷創新高",
        "explain5" : "當利率小於通膨，存入銀行的錢會減少，流通在市場上的錢會增加，這會加速通膨",
        "explain6" : "因此當高通膨發生時，需要升息讓利率(FED) >= 通膨(CPI)藉此降低通膨，",
        "explain7" : "可以透過下圖幾個指標觀察通膨是否被控制"
    },
    "AHE_explain" : {
        "explain0" : "在3.5%到4%以下代表沒有發生通膨",
        "explain1" : "升息必須升到通膨明顯下降且持續",
    }
}
