    }

    return (
        <>
            <Backdrop
                sx = {{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                open = { loading }
            >
                資料載入中&emsp;
                <CircularProgress color = "inherit" />
            </Backdrop>

            <div className = 'row mx-auto py-3'>
                <div className = 'col-md-10 mx-auto'>
                    <h3 className = "text-center">個股研究報告資料庫查詢</h3>

                    <form onSubmit = { submit }>
                    <div className = 'form-group row py-3'>
                        <label htmlFor = "columns" className = "col-md-3 col-form-label text-center">搜尋欄位:</label>
                        <div className = 'col-md-3'>
                            <select id = "columns" className = "form-select">
                                <option value = "title">新聞標題</option>
                                <option value = "repoter">記者</option>
                            </select>
                        </div>

                        <div className = 'col-md-6'>
                            <input type = "text" className = "form-control"></input>
                        </div>
                    </div>
                    </form>
                </div>
            </div>
        </>
    );
}

export default FinancialDataComp;
