import React, { useContext } from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';

function CurrentConditionComp() {
    const {conditions, setConditions} = useContext(ConditionsContext);

    return (
        <>
            { conditions.length === 0 && <p className = 'text-center' style = {{ color : "red" }}>尚未選擇條件</p> }
            { conditions.length !== 0 && 
                <List dense component = "div" role = "list">
                    { conditions.map((item, i) => {
                        return(
                            <ListItem key = { i } role = "listitem" button = { true }>
                                <ListItemText primary = { item } />
                            </ListItem>
                        )
                    }) }
                </List>
            }
        </>
    );
}

export default CurrentConditionComp;
