import React, { useContext } from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import { ConditionsContext } from './chooseTikcerComp';

function TechnicalAnalyzeComp() {
    const {conditions, setConditions} = useContext(ConditionsContext);

    const condition = ["5ma突破30ma黃金交叉", "5ma突破30ma死亡交叉", "KD黃金交叉"]

    const handleToggle = (e) => {
        if((!conditions.includes(e.target.innerText)) && (conditions.length < 3)){
            conditions.push(e.target.innerText)
            setConditions(conditions)
        }
    }

    return (
        <>
            <List component = "div" role = "list">
                {condition.map((item, i) => {
                    return(
                        <ListItem key = { i } role = "listitem" button = { true } onClick = { e => handleToggle(e) }>
                            <ListItemText primary = { item } />
                        </ListItem>
                    )
                })}
            </List>
        </>
    );
}

export default TechnicalAnalyzeComp;
