import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { config } from '../../constant';
import { columns_financialData, columns_popular_ticker } from '../column/column';
import { StripedDataGrid } from '../stripedDataGrid/stripedDataGrid';

function PopularTicker() {
    const [pageSize, setPageSize] = useState(5)
    const [data, setData] = useState([])
    const [loading, setLoading] = useState(false)
    const [pageDetail, setPageDetail] = useState(0)
    const [pageSizeDetail, setPageSizeDetail] = useState(10)
    const [type, setType] = useState("financialData")

    useEffect(() => {
        axios.get(config["rootApiIP"] + "/data/popular_ticker")
        .then(res => {
            setData(res.data)
        }).catch(res => {
            if(res.response.data === "Session expired") window.location.reload()
        })
    }, [])

    return (
        <>
            <div className = 'row mx-auto'>
                <div className = 'col-md-6 mx-auto py-3'>
                    <h3 className = "display-6 text-center">一週內熱門個股Top5</h3>

                    <StripedDataGrid
                        columns = { columns_popular_ticker(setLoading, setType) }
                        rows = { data }
                        pageSize = { pageSize }
                        onPageSizeChange = { (newPageSize) => setPageSize(newPageSize) }
                        rowsPerPageOptions = {[5]}
                        getRowId = { row => row.stock_num }
                        pagination
                        autoHeight
                        disableColumnMenu
                        disableColumnSelector
                        disableDensitySelector
                        disableColumnFilter
                        disableSelectionOnClick = { true }
                        getRowClassName={(params) =>
                            params.indexRelativeToCurrentPage % 2 === 0 ? 'even' : 'odd'
                        }
                    />
                </div>
            </div>

            <Backdrop
                sx = {{ zIndex: (theme) => theme.zIndex.drawer + 1 }}
                open = { loading }
            >
                <div className = 'row mx-auto'>
                    <div className = 'col-md-8 mx-auto'>
                        { type === "financialData" && 
                        <DataGrid
                            columns = { columns_financialData }
                            rows = { data["financialData"] }
                            page = { pageDetail }
                            onPageChange = {(newPage) => setPageDetail(newPage)}
                            pageSize = { pageSizeDetail }
                            onPageSizeChange = { (newPageSize) => setPageSizeDetail(newPageSize) }
                            rowsPerPageOptions = {[5, 10, 20]}
                            getRowId = { row => row.ID }
                            components = {{ Toolbar: GridToolbar }}
                            componentsProps = {{ toolbar: { showQuickFilter: true },}}
                            pagination
                            disableColumnMenu
                            disableColumnSelector
                            disableDensitySelector
                            disableColumnFilter
                            disableSelectionOnClick = { true }
                            autoHeight
                        />}

                        { type === "news" && 
                        <DataGrid
                            columns = { columns_financialData }
                            rows = { data["financialData"] }
                            page = { pageDetail }
                            onPageChange = {(newPage) => setPageDetail(newPage)}
                            pageSize = { pageSizeDetail }
                            onPageSizeChange = { (newPageSize) => setPageSizeDetail(newPageSize) }
                            rowsPerPageOptions = {[5, 10, 20]}
                            getRowId = { row => row.ID }
                            components = {{ Toolbar: GridToolbar }}
                            componentsProps = {{ toolbar: { showQuickFilter: true },}}
                            pagination
                            disableColumnMenu
                            disableColumnSelector
                            disableDensitySelector
                            disableColumnFilter
                            disableSelectionOnClick = { true }
                            autoHeight
                        />}
                    </div>
                </div>
            </Backdrop>
        </>
    );
}

export default PopularTicker;
