import React, { useState, useEffect, createContext } from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import NavbarComp from './component/navbar/navbar';
import axios from "axios";
import { rootApiIP, WebSocketIP } from './constant';
import socketio from 'socket.io-client'

function PrivateRoute() {
    const [isAuth, setIsAuth] = useState()
    const [ws, setWs] = useState(null)
    const WSContext = createContext()

    useEffect(() => {
        axios.get(rootApiIP + "/data/isAuth")
        .then(res => {
            setIsAuth(true)
        }).catch(res => {
            alert("Session expired, please login again")
            setIsAuth(false)
        })

        axios.get(rootApiIP + "/data/username")
        .then(res => {
            setWs(socketio.connect(WebSocketIP, {
                extraHeaders : {
                    "username" : res.data
                }
            }))
        }).catch(res => {
        })

    }, [])

    if (isAuth === undefined) return null

    return isAuth ? <><NavbarComp /> <Outlet /></> : <Navigate to = "/login" />;
}

export default PrivateRoute;
