import React, { useEffect, useState } from 'react';

function NavbarNotifyComp(props) {
    const [read, setRead] = useState(false)
    const [notifyList, setNotifyList] = useState([])

    function handleUnread(){
        if(read === true){
            setRead(false)

            //update notifyList
            let temp1 = []

            for(let i = 0; i < 5; i++){
                temp1.push(
                    <div className = 'card-body' key = { i }>
                        <h3 className = 'card-title'>2330</h3>
                        <h6 className = "card-subtitle text-muted">穿越地板線</h6>
                        <h6 className = "card-subtitle text-muted">2022/10/0{i}</h6>
                    </div>
                )

                if(i === 4){
                    setNotifyList(temp1)
                }
            };
        }
    }

    function handleRead(){
        if(read === false){
            setRead(true)

            //update notifyList
            let temp2 = []

            for(let i = 0; i < 5; i++){
                temp2.push(
                    <div className = 'card-body' key = { i }>
                        <h3 className = 'card-title'>2330</h3>
                        <h6 className = "card-subtitle text-muted">穿越地板線</h6>
                        <h6 className = "card-subtitle text-muted">2022/09/0{i}</h6>
                    </div>
                )

                if(i === 4){
                    setNotifyList(temp2)
                }
            };
        }
    }

    useEffect(() => {
        let temp = []

        for(let i = 0; i < 5; i++){
            temp.push(
                <div className = 'card-body' key = { i }>
                    <h3 className = 'card-title'>2330</h3>
                    <h6 className = "card-subtitle text-muted">穿越地板線</h6>
                </div>
            )

            if(i === 4){
                setNotifyList(temp)
            }
        };
    }, [])

    return (
        <>
            <div className = " container" style = {{ width : "200px" }}>
                <div className = 'row'>
                    <div className = 'col text-center' style = {{ cursor : "pointer" , borderRight: "1px solid #ADADAD"}} onClick = { () => handleUnread() }>
                        未讀
                    </div>

                    <div className = 'col text-center' style = {{ cursor : "pointer" }} onClick = { () => handleRead() }>
                        已讀
                    </div>
                </div>

                <hr />

                <div className = 'row' id = "NotifyList">
                    {notifyList}
                </div>
            </div>
        </>
    );
}

export default NavbarNotifyComp;
