import React, { useEffect, useState } from 'react';
import List from '@mui/material/List';

function NavbarNotifyComp(props) {
    const [allRead, setAllRead] = useState("all")
    const [notifyList, setNotifyList] = useState([])
    const [init, setInit] = useState(false)

    function handleAll(){
        if(allRead === "read"){
            setAllRead("all")

            //update notifyList
            let temp1 = []

            for(let i = 0; i < 10; i++){
                temp1.push(
                    <div className = 'card' key = { i }>
                        <div className = 'card-body'>
                            <h3 className = 'card-title'>2330</h3>
                            <h6 className = "card-subtitle text-muted">穿越地板線</h6>
                            <h6 className = "card-subtitle text-muted">2022/10/0{i + 1}</h6>
                        </div>
                    </div>
                )

                if(i === 4){
                    setNotifyList(temp1)
                }
            };
        }
    }

    function handleRead(){
        if(allRead === "all"){
            setAllRead("read")
            //update notifyList
            let temp2 = []

            for(let i = 0; i < 10; i++){
                temp2.push(
                    <div className = 'card' key = { i }>
                        <div className = 'card-body'>
                            <h3 className = 'card-title'>2330</h3>
                            <h6 className = "card-subtitle text-muted">穿越地板線</h6>
                            <h6 className = "card-subtitle text-muted">2022/09/0{i + 1}</h6>
                        </div>
                    </div>
                )

                if(i === 4){
                    setNotifyList(temp2)
                }
            };
        }
    }

    useEffect(() => {
        if(init === false){
            setInit(true)

            let temp = []

            for(let i = 0; i < 10; i++){
                temp.push(
                    <div className = 'card' key = { i } style = {{ cursor : "pointer" }}>
                        <div className = 'card-body'>
                            <h3 className = 'card-title'>2330</h3>
                            <h6 className = "card-subtitle text-muted">穿越地板線</h6>
                            <h6 className = "card-subtitle text-muted">2022/10/0{i + 1}</h6>
                        </div>
                    </div>
                )

                if(i === 4){
                    setNotifyList(temp)
                }
            };
        }else{
            if(allRead === "all"){
                handleAll()
            }else{
                handleRead()
            }
        }
    }, [])

    return (
        <>
            <div className = " container mx-auto" style = {{ width : "20vw" }}>
                <div className = 'row'>
                    <h2>通知</h2>
                </div>

                <div className = 'row' style = {{ minHeight : "3vh" }}>
                    { allRead === "all" ? 
                        <div className = 'col-md-3 text-center offset-md-1' style = {{ cursor : "pointer" , border: "1px solid #ADADAD", backgroundColor : "gray"}} onClick = { () => handleAll() }>
                            全部
                        </div> :

                        <div className = 'col-md-3 text-center offset-md-1' style = {{ cursor : "pointer" , border: "1px solid #ADADAD"}} onClick = { () => handleAll() }>
                            全部
                        </div>
                    }

                    { allRead === "read" ? 
                        <div className = 'col-md-3 text-center' style = {{ cursor : "pointer" , border: "1px solid #ADADAD", backgroundColor : "gray"}} onClick = { () => handleRead() }>
                            已讀
                        </div> :

                        <div className = 'col-md-3 text-center' style = {{ cursor : "pointer" , border: "1px solid #ADADAD"}} onClick = { () => handleRead() }>
                            已讀
                        </div>
                    }
                </div>

                <div className = 'row'>
                    <h3>先前通知</h3>
                </div>

                <div className = 'row' id = "NotifyList"  style = {{ width : "20vw" }}>
                    <List
                        sx = {{
                            width: '100%',
                            bgcolor: 'background.paper',
                            position: 'relative',
                            overflow: 'auto',
                            maxHeight: "80vh",
                        }}
                    >
                        {notifyList}
                    </List>
                </div>
            </div>
        </>
    );
}

export default NavbarNotifyComp;
