import React, { useEffect, useMemo, useState } from 'react';
import axios from 'axios';
import bgimage from "../../image/coins_on_chart.jpg"
import DataTable from 'react-data-table-component';
axios.defaults.withCredentials = true;

function HomeComp() {
    let [data, setData] = useState([]);
    const [filterText, setFilterText] = React.useState('');
    const [resetPaginationToggle, setResetPaginationToggle] = useState(false);

    const FilterComponent = ({ filterText, onFilter, onClear }) => (
        	<>
        		<TextField
        			id="search"
        			type="text"
        			placeholder="Filter By Name"
        			aria-label="Search Input"
        			value={filterText}
        			onChange={onFilter}
        		/>
        		<ClearButton type="button" onClick={onClear}>
        			X
        		</ClearButton>
        	</>
        );

    const subHeaderComponentMemo = useMemo(() => {
        const handleClear = () => {
            if (filterText) {
                setResetPaginationToggle(!resetPaginationToggle);
                setFilterText('');
            }
        };

        return (
            <FilterComponent onFilter = {e => setFilterText(e.target.value)} onClear={handleClear} filterText = { filterText } />
        );
    }, [filterText, resetPaginationToggle]);

    const columns = [
        {
            name : "股票代號",
            selector : row => row.stockNum,
            sortable: true,
        },
        {
            name : "股票名稱",
            selector : row => row.stockName,
            sortable: true,
        },
        {
            name : "資料日期",
            selector : row => row.date,
            sortable: true,
        },
        {
            name : "提供者",
            selector : row => row.investmentCompany,
            sortable: true,
        },
        {
            name : "推薦",
            selector : row => row.recommend,
            sortable: true,
        },
        {
            name : "檔案下載",
            selector : row => row.filePath,
            cell : rowData => <a href = { "http://140.116.214.154:3000/api/data/download?filePath=" + rowData.filePath } download = { rowData.filePath.split("/")[-1] }>download</a>
        },
    ]

    useEffect(() => {
        axios.get("http://140.116.214.154:3000/api/data/first")
        .then(res => {
            setData(res.data);
        }).catch(res => {
        })
    }, [])

    return (
        <>
            <div className = 'jumbotron jumbotron-fluid d-flex' style = {{ backgroundImage : `url(${bgimage})`, opacity : "0.8", backgroundAttachment : "fixed", backgroundSize : "cover", height : "40vh", justifyContent : "center", alignItems : "center" }}>
                <div className = 'text-center'>
                    <h1 className = "display-4" style = {{ color : "white" }}>Financial Database</h1>
                    <p className = 'lead' style = {{ color : "white" }}>Easily and quickly</p>
                </div>
            </div>


            <div className = 'container-fluid'>
                <div className = 'col-md-10 offset-md-1'>
                    <h3 className = "display-4 text-center">最新15筆資料</h3>

                    <DataTable
                        columns = { columns }
                        data = { data }
                        pagination
                    />
                </div>
            </div>
        </>
    );
}

export default HomeComp;
