import { Backdrop, Button, CircularProgress, List, ListItemButton, ListItemText } from '@mui/material';
import { DataGrid, GridToolbar } from '@mui/x-data-grid';
import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { rootApiIP } from '../../constant';
import { columns_news, columns_statementdog } from '../column/column';
import NewsItem from './newsItem';

function NewsComp() {
    var Today = new Date()
    const todayDate = Today.getFullYear() + "-" + String(Today.getMonth()+1).padStart(2, '0') + "-" + String(Today.getDate()).padStart(2, '0')

    const [loading, setLoading] = useState(false)
    const [data0, setData0] = useState([])
    const [page0, setPage0] = useState(0)
    const [pageSize0, setPageSize0] = useState(10)

    const [data1, setData1] = useState([])
    const [page1, setPage1] = useState(0)
    const [pageSize1, setPageSize1] = useState(10)

    const [data, setData] = useState([])

    const columns_summary_news = [
        { field: "category", headerName : "新聞類別", flex: 1, headerAlign: 'center', align: 'center' },
        { field: "todayQuantity", headerName : todayDate + "新聞數量", flex: 1, headerAlign: 'center', align: 'center', renderCell : 
            rowData => <Button variant = "text" onClick = { () => buttonTodayHandle(rowData["row"]["category"]) }>{rowData.value}</Button>
        },
        { field: "pastQuantity", headerName : todayDate + "前新聞數量", flex: 1, headerAlign: 'center', align: 'center', renderCell : 
            rowData => <Button variant = "text" onClick = { () => buttonPastHandle(rowData["row"]["category"]) }>{rowData.value}</Button>
        }
    ];

    const columns_summary_statementdog = [
        { field: "category", headerName : "新聞類別", flex: 1, headerAlign: 'center', align: 'center' },
        { field: "todayQuantity", headerName : todayDate + "新聞數量", flex: 1, headerAlign: 'center', align: 'center', renderCell : 
            rowData => <Button variant = "text" onClick = { () => buttonTodayHandle(rowData["row"]["category"]) }>{rowData.value}</Button>
        },
        { field: "pastQuantity", headerName : todayDate + "前新聞數量", flex: 1, headerAlign: 'center', align: 'center', renderCell : 
            rowData => <Button variant = "text" onClick = { () => buttonPastHandle(rowData["row"]["category"]) }>{rowData.value}</Button>
        }
    ];

    const buttonTodayHandle = (buttonCategory) => {
        setLoading(true)

        axios.get(rootApiIP + "/data/news_search_today", { params : {
            "date" : todayDate,
            "category" : buttonCategory
        }})
        .then((res) => {
            setData(res.data)
            setLoading(false)
            window.scrollTo({
                left : 0, 
                top : document.body.scrollHeight,
                behavior : "smooth"
            })
        })
        .catch((res) => {
            if(res.response.data === "Session expired") window.location.reload()
            setLoading(false)
        })
    }

    const buttonPastHandle = (buttonCategory) => {
        setLoading(true)

        axios.get(rootApiIP + "/data/news_search_past", { params : {
            "date" : todayDate,
            "category" : buttonCategory
        }})
        .then((res) => {
            setData(res.data)
            setLoading(false)
            window.scrollTo({
                left : 0, 
                top : document.body.scrollHeight,
                behavior : "smooth"
            })
        })
        .catch((res) => {
            if(res.response.data === "Session expired") window.location.reload()
            setLoading(false)
        })
    }

    useEffect(() => {
        axios.get(rootApiIP + "/data/news_summary", {params :{
            "date" : todayDate,
        }})
        .then((res) => {
            setData0(res.data)
        })
        .catch((res) => {
            if(res.response.data === "Session expired") window.location.reload()
        })

        axios.get(rootApiIP + "/data/news_summary_statementdog", {params :{
            "date" : todayDate,
        }})
        .then((res) => {
            setData1(res.data)
        })
        .catch((res) => {
            if(res.response.data === "Session expired") window.location.reload()
        })
    }, [])

    return (
        <>
            <Backdrop
                sx = {{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                open = { loading }
            >
                資料載入中&emsp;
                <CircularProgress color = "inherit" />
            </Backdrop>

            <div className = 'row mx-auto py-3' style = {{ width : "90vw" }}>
                <h3 className = "display-6 text-center">新聞資料庫總覽</h3>

                <div className = 'col-md-7 mx-auto pt-3'>
                    <DataGrid
                        columns = { columns_summary_news }
                        rows = { data0 }
                        page = { page0 }
                        onPageChange={(newPage) => setPage0(newPage)}
                        pageSize = { pageSize0 }
                        onPageSizeChange={ (newPageSize) => setPageSize0(newPageSize) }
                        rowsPerPageOptions = {[5, 10, 20]}
                        getRowId = { row => row.ID }
                        components = {{ Toolbar: GridToolbar }}
                        componentsProps = {{ toolbar: { showQuickFilter: true },}}
                        pagination
                        autoHeight
                        disableColumnMenu
                        disableColumnSelector
                        disableDensitySelector
                        disableColumnFilter
                        disableSelectionOnClick = { true }
                    />
                </div>

                <div className = 'col-md-5 mx-auto pt-3'>
                    <DataGrid
                        columns = { columns_summary_statementdog }
                        rows = { data1 }
                        page = { page1 }
                        onPageChange={(newPage) => setPage1(newPage)}
                        pageSize = { pageSize1 }
                        onPageSizeChange={ (newPageSize) => setPageSize1(newPageSize) }
                        rowsPerPageOptions = {[5, 10, 20]}
                        getRowId = { row => row.ID }
                        components = {{ Toolbar: GridToolbar }}
                        componentsProps = {{ toolbar: { showQuickFilter: true },}}
                        pagination
                        autoHeight
                        disableColumnMenu
                        disableColumnSelector
                        disableDensitySelector
                        disableColumnFilter
                        disableSelectionOnClick = { true }
                    />
                </div>
            </div>

            <h3 className = "display-6 text-center py-3">新聞資料庫查詢</h3>

            <div className = 'row mx-auto'>
                <div className = 'col-md-8 mx-auto'>
                    <div className = "card h-100 p-0 mt-3">
                    <div className = 'mx-3 mt-2'>
                            <ul className = "nav nav-tabs">
                                <li className = "nav-item">
                                    <button className = "nav-link active" data-bs-toggle = "tab" onClick = {() => setType("news")}>新聞</button>
                                </li>

                                <li className = "nav-item">
                                    <button className = "nav-link" data-bs-toggle = "tab" onClick = {() => setType("research")}>個股研究報告</button>
                                </li>

                                <li className = "nav-item">
                                    <button className = "nav-link" data-bs-toggle = "tab" onClick = {() => setType("calendar")}>法說會行事曆</button>
                                </li>

                                <li className = "nav-item">
                                    <button className = "nav-link" data-bs-toggle = "tab" onClick = {() => setType("dbSearch")}>綜合資料庫查詢</button>
                                </li>
                            </ul>

                            { type === "news" && <NewsItem data = { news } />}
                            { type === "research" && <ReasearchItem data = { research } />}
                            { type === "calendar" && <CalendarItem />}
                            { type === "dbSearch" && <DbSearchItem /> }
                        </div>
                    </div>
                </div>
            </div>
            <NewsItem setLoading = { setLoading } data = { data } setData = { setData }/>
        </>
    );
}

export default NewsComp;
