export const PerRiverInit = {
    "PER_table" : {"data" : []},
    "NewPrice" : 0,
    "cheap" : 0,
    "reasonable" : 0,
    "expensive" : 0,
    "down_cheap" : [0],
    "cheap_reasonable" : [0],
    "reasonable_expensive" : [0],
    "up_expensive" : [0],
}

export function PerRiverExplain(label, EPS){
    return(
        {
            "explain1" : "將歷史本益比最高到最低區間平分為4份共6個區間, 再分別乘上EPS估算股價",
            "explain2" : `股價 = EPS x 本益比，越接近底部越便宜，反之越貴`,
            "explain3" : `便宜價 = EPS x 本益比 = ${EPS * (label[0].slic(0, label[0].length - 1))}`,
        }
    )
}
