import React, { useState } from 'react';
import Pagination from '@mui/material/Pagination';

function CustomListComp(props) {
    const [pageSize, setPageSize] = useState(8)
    const [page, setPage] = useState(1);
    const [data, setData] = useState({...props.data});

    React.useEffect(() => {
        setData(props.data.slice(0, pageSize));
    }, [pageSize]);

    const handleChange = (event, value) => {
        setPage(value);
        setData(props.data.slice(pageSize * (value - 1), pageSize * value));
    };
    console.log(data)
    return (
        <>
            <div className = 'row'>
                { data.slice(0 , 4).map((d, i) => (
                    <div key = { i } className = 'col' style = {{ borderStyle : 'solid', borderWidth : "1px", margin : "5px" }}>{ d.name }</div>
                )) }
            </div>

            <div className = 'row'>
                { data.slice(4 , 8).map((d, i) => (
                    <div key = { i } className = 'col'  style = {{ borderStyle : 'solid', borderWidth : "1px", margin : "5px" }}>{ d.name }</div>
                )) }
            </div>

            <Pagination
                count = { Math.ceil(props.data.length / 8) }
                page = { page }
                onChange = { handleChange }
                showFirstButton
                showLastButton
            />
        </>
    );
}

export default CustomListComp;
