import React, { useState } from 'react';
import axios from 'axios';
import { config } from '../../constant';

function RegisterItem() {
    const [name, setName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const [nameError, setNameError] = useState(false)
    const [usernameError, setUsernameError] = useState(false)
    const [emailError, setEmailError] = useState(false);
    const [passwordError, setPasswordError] = useState(false);
    const [confirmpasswordError, setconfirmpasswordError] = useState(false);

    function register(){
        axios.post(config["rootApiIP"] + "/user/register", {
            name : name,
            userName : username,
            email : email,
            password : password
        }).then(res => {
            alert("Register successfully")
        }).catch(err => {
            if(err.response.status === 401){
                alert(err.response.data)
            }else{
                alert("error")
            }
        });
    };

    function nameChangeHandle(name){
        if(name.length > 20){
            setNameError(true)
        }else{
            setName(name)
            setNameError(false)
        }
    }

    function usernameChangeHandle(username){
        if(username.length > 20){
            setUsernameError(true)
        }else{
            setUsername(username)
            setUsernameError(false)
        }
    }

    function emailChangeHandle(email){
        var pattern = new RegExp(/^([A-Za-z0-9_\.-]+)@([\da-z\.-]+)\.([a-z\.]{3,6})$/);//eslint-disable-line

        if(pattern.test(email)){
            setEmail(email)
            setEmailError(false)
        }else{
            setEmailError(true)
        };
    };

    function passwordChangeHandle(password){
        var pattern = new RegExp(/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{6,15}$/);//eslint-disable-line

        if(pattern.test(password)){
            if(password !== confirmPassword){
                setconfirmpasswordError(true)
            }else{
                setconfirmpasswordError(false)
            };
            setPassword(password)
            setPasswordError(false)
        }else{
            setPasswordError(true)
        };
    };

    function confirmPasswordChangeHandle(confirmPassword){
        if(confirmPassword !== password){
            setconfirmpasswordError(true)
        }else{
            setConfirmPassword(confirmPassword)
            setconfirmpasswordError(false)
        };
    };

    function submit(e){
        e.preventDefault()

        if(emailError === false && passwordError === false && confirmpasswordError === false && nameError === false && usernameError === false && name !== "" && username !== "" && email !== "" && password !== "" && confirmPassword !== ""){
            register();
        }
    }

    return (
        <form className = "row g-3 needs-validation px-4" onSubmit = { submit } noValidate>
            <div className = "form-row">
                <h2 className = "text-center display-4">Register</h2>
            </div>

            <div className = "form-row">
                <p style = {{ color : "red" }}>* 表示必填欄位</p>
                <div className = "form-group">
                    <label htmlFor = "name">名字 <span style = {{ color : "red" }}>*</span></label>
                    <input type = "text" className = "form-control" id = "name" onChange = { event => nameChangeHandle(event.target.value) } required/>
                    <div className = "form-text">長度不能超過20</div>
                    { nameError ? <div className = "item"> <p style = {{ color : "red" }}>Name is too long</p> </div> : <div></div> }
                </div>
            </div>

            <div className = "form-row">
                <div className = "form-group">
                    <label htmlFor = "account">使用者名稱 <span style = {{ color : "red" }}>*</span></label>
                    <input type = "text" className = "form-control" id = "account" onChange = { event => usernameChangeHandle(event.target.value) } required/>
                    <div className = "form-text">長度不能超過20</div>
                    { usernameError ? <div className = "item"> <p style = {{ color : "red" }}>username is too long</p> </div> : <div></div> }
                </div>
            </div>

            <div className = "form-row py-1">
                <div className = "form-group">
                    <label htmlFor = "email">Email <span style = {{ color : "red" }}>*</span></label>
                    <input type = "text" className = "form-control" id = "email" onChange = { event => emailChangeHandle(event.target.value) } required/>
                    { emailError ? <div className = "item"> <p style = {{ color : "red" }}>Email format error</p> </div> : <div></div> }
                </div>
            </div>

            <div className = "form-row">
                <div className = "form-group">
                    <label htmlFor = "password">密碼 <span style = {{ color : "red" }}>*</span></label>
                    <input type = "password" className = "form-control" id = "password" onChange = { event => passwordChangeHandle(event.target.value) }/>
                    <div className = "form-text">密碼長度至少6個字元 最多不超過15個</div>
                    <div className = "form-text">密碼必須包含至少一個大寫及小寫</div>
                    { passwordError ? <div className = "item"> <p style = {{ color : "red" }}>密碼格式錯誤</p> </div> : <div></div> }
                </div>
            </div>

            <div className = "form-row">
                <div className = "form-group">
                    <label htmlFor = "confirmPassword">確認密碼 <span style = {{ color : "red" }}>*</span></label>
                    <input type = "password" className = "form-control" id = "confirmPassword" onChange = { event => confirmPasswordChangeHandle(event.target.value) }/>
                    { confirmpasswordError ? <div className = "item"> <p style = {{ color : "red" }}>密碼及確認密碼不相同</p> </div> : <div></div> }
                </div>
            </div>

            <div className = "d-grid py-3">
                {emailError === false && passwordError === false && confirmpasswordError === false && nameError === false && usernameError === false ? <button type = "submit" className = "btn btn-primary">註冊</button> : <button type = "submit" className = "btn btn-primary" disabled>註冊</button>}
            </div>
        </form>
    );
}

export default RegisterItem;
