import React, { useState, useEffect } from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import NavbarComp from './component/navbar/navbar';
import axios from "axios";
import { rootApiIP } from './constant';
import { createContext } from 'react'
import socketio from 'socket.io-client'
import { WebSocketIP } from './constant'

function PrivateRoute() {
    const [isAuth, setIsAuth] = useState()
    const [ws, swtWS] = useState(null)
    const WSContext = createContext()

    useEffect(() => {
        async function state(){
            await axios.get(rootApiIP + "/data/isAuth")
            .then(res => {
                setIsAuth(true)
            }).catch(res => {
                alert("Session expired, please login again")
                setIsAuth(false)
            })
        }

        state()
    }, [])

    if (isAuth === undefined) return null

    if(isAuth){
        if(ws === null) setWS
        return <WSContext.Provider value = {socketio.connect(WebSocketIP)}><NavbarComp /> <Outlet /></WSContext.Provider>
    }else{
        // socketio.disconnect(WebSocketIP)
        return <Navigate to = "/login" />
    }
}

export default PrivateRoute;
