import { Backdrop, CircularProgress } from '@mui/material';
import React, { useState } from 'react';

function NewsComp() {
    const [loading, setLoading] = useState(false)

    const submit = (e) => {
        e.preventDefault()
    }

    return (
        <>
            <Backdrop
                sx = {{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                open = { loading }
            >
                資料載入中&emsp;
                <CircularProgress color = "inherit" />
            </Backdrop>

            <div className = 'row mx-auto py-3' style = {{ width : "50vw" }}>
                <h3 className = "display-6 text-center">新聞資料庫查詢</h3>

                <form onSubmit = { submit }>
                    <label htmlFor = "columns" className = "col-md-3 col-form-label text-center">MA類型:</label>
                    <div className = 'col-md-3'>
                        <select id = "columns" className = "form-select" onChange = {e =>{
                            setMaType(e.target.value)
                            setOverVolume(-1)
                        }}>
                            <option value = "sma">sma</option>
                            <option value = "wma">wma</option>
                        </select>
                    </div>
                </form>
            </div>
        </>
    );
}

export default NewsComp;
