import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Typeahead } from 'react-bootstrap-typeahead';
import { rootApiIP } from '../../constant'
import PricingComp from './pricingComp';
import { priceInit, pricing1 } from './pricingExplain';

function StockPricingStratagyComp() {
    const [stockNum, setStockNum] = useState([]);
    const [year, setYear] = useState("1");
    const [autocom, setAutocom] = useState([]);
    const [price, setPrice] = useState(priceInit);
    const [loading, setLoading] = useState(false);
    const [inputError, setInputError] = useState(false);

    useEffect(() => {
        axios.get(rootApiIP + "/data/autoCom")
        .then(res => {
            setAutocom(res.data);
        }).catch(res => {
            if(res.response.data === "Session expired") window.location.reload()
        })
    }, [])

    function submit(e){
        e.preventDefault();

        if(stockNum !== ""){
            // const formData = new FormData();
            // formData.append("title", title);
            // formData.append("selectFile", file);
            // formData.append("filename", fileName);

            // axios.post(rootApiIP + "/data/upload/industry_analysis_upload", formData, {
            //     headers : { "Content-Type": "multipart/form-data" }
            // }).then(res => {
            //     alert("上傳成功")
            // }).catch(res => {
            //     if(res.response.data === "Session expired") window.location.reload()
            // })
        }else{
            alert("請選擇檔案")
        }
    }

    return (
        <>
            <div className = 'row mx-auto py-3' style = {{ width : "40vw" }}>
                <h3 className = "display-6 text-center">股票定價</h3>

                <form onSubmit = { submit }>
                    <div className = 'form-group row'>
                        <label htmlFor = "stockNum_or_Name" className = "col-md-3 col-form-label text-center">股票代號&名稱:&emsp;</label>

                        <div className = 'col-md-4'>
                            <Typeahead
                                id = "stockNum_or_Name"
                                labelKey = "stock_num_name"
                                onChange = { setStockNum }
                                options = { autocom }
                                placeholder = "請輸入股票代號或名稱"
                                selected = { stockNum }
                            />
                        </div>

                        <label htmlFor = "year" className = "col-md-1 col-form-label text-center">日期:</label>
                        <div className = 'col-md-1'>
                        <select id = "provider" className = "form-select" onChange = {e => setInput4(e.target.value)}>
                                    <option value = "">請選擇券商</option>
                                    <option value = "台新投顧">台新投顧</option>
                                    <option value = "中信投顧">中信投顧</option>
                                    <option value = "元富">元富</option>
                                    <option value = "國票">國票</option>
                                    <option value = "元大">元大</option>
                                    <option value = "統一投顧">統一投顧</option>
                                    <option value = "第一金">第一金</option>
                                    <option value = "富邦台灣">富邦台灣</option>
                                    <option value = "SinoPac">永豐金證券</option>
                                </select>
                        </div>

                        { inputError ? <p style = {{ color : "red" }}>股票代號&名稱格式錯誤</p> : <></> }
                        { loading && <button type = "submit" className = "btn btn-primary" style = {{ width : "150px" }} disabled><span className = "spinner-border spinner-border-sm" role = "status" aria-hidden = "true"></span></button> }
                        { !loading && <button type = "submit" className = "btn btn-primary" style = {{ width : "150px" }}>搜尋</button> }
                    </div>
                </form>
            </div>

            <div className = 'row mx-auto py-3' style = {{ width : "90%" }}>
                <div className = 'col-md-3'>
                    <PricingComp pricingName = "股利法" pricingExplain = { pricing1 } lowPrice = { price["cheap1"] } resonablePrice = { price["resonable1"] } highPrice = { price["expensive1"] }/>
                </div>

                <div className = 'col-md-3'>
                    <PricingComp pricingName = "高低價法" pricingExplain = { pricing1 } lowPrice = { price["cheap2"] } resonablePrice = { price["resonable2"] } highPrice = { price["expensive2"] }/>
                </div>

                <div className = 'col-md-3'>
                    <PricingComp pricingName = "本益比法" pricingExplain = { pricing1 } lowPrice = { price["cheap3"] } resonablePrice = { price["resonable3"] } highPrice = { price["expensive3"] }/>
                </div>

                <div className = 'col-md-3'>
                    <PricingComp pricingName = "本淨比法" pricingExplain = { pricing1 } lowPrice = { price["cheap4"] } resonablePrice = { price["resonable4"] } highPrice = { price["expensive4"] }/>
                </div>
            </div>
        </>
    );
}

export default StockPricingStratagyComp;
