import React, { useState, useEffect } from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import NavbarComp from './component/navbar/navbar';
import axios from "axios";
import { rootApiIP } from './constant';

function PrivateRoute() {
    const [isAuth, setIsAuth] = useState()

    function logout(e){
        e.preventDefault()

        axios.get(rootApiIP + "/user/logout")
        .then(res => {
            nav("/login")
        }).catch(res => {
            if(res.response.data === "Session expired") window.location.reload()
            alert("something error, please try again")
        })
    }

    window.addEventListener("beforeunload", (ev) => {  
        ev.preventDefault();
        return ev.returnValue = 'Are you sure you want to close?';
    });

    useEffect(() => {
        async function state(){
            await axios.get(rootApiIP + "/data/isAuth")
            .then(res => {
                setIsAuth(true)
            }).catch(res => {
                alert("Session expired, please logon again")
                setIsAuth(false)
            })
        }

        state()
    }, [])

    if (isAuth === undefined) return null

    return isAuth ? <div><NavbarComp /> <Outlet /></div> : <Navigate to = "/login" />;
}

export default PrivateRoute;
