import axios from 'axios';
import React, { useState } from 'react';
import { rootApiIP } from '../../constant';

function SelfUploadPage() {
    const [file, setFile] = useState(null);
    const [fileName, setFileName] = useState("");
    const [provider, setProvider] = useState("台新投顧")
    const [evaluate, setEvaluate] = useState("台新投顧")

    const saveFile = (e) => {
        if(e.target.files.length !== 0){
            setFile(e.target.files[0]);
            setFileName(e.target.files[0].name);
        }else{
            setFile(null);
            setFileName("");
        }
    };

    function submit(e){
        e.preventDefault();

        if(fileName !== ""){
            const formData = new FormData();
            formData.append("selectFile", file);
            formData.append("filename", fileName);

            axios.post(rootApiIP + "/data/upload/meeting_data_upload", formData, {
                headers : { "Content-Type": "multipart/form-data" }
            }).then(res => {
                alert("上傳成功")
            }).catch(res => {
                if(res.response.data === "Session expired") window.location.reload()
            })
        }else{
            alert("請選擇檔案")
        }
    }
    return (
        <>
            <div className = 'row mx-auto py-3' style = {{ width : "50vw" }}>
                <h3 className = "display-6 text-center">自定義上傳</h3>

                <form onSubmit = { submit }>
                    <div className = 'form-group row'>
                        <label htmlFor = "provider" className = "col-md-2 col-form-label text-center">券商名稱:</label>
                        <div className = 'col-md-3'>
                            <select id = "provider" className = "form-select" onChange = {e => setProvider(e.target.value)}>
                                <option value = "台新投顧">台新投顧</option>
                                <option value = "中信投顧">中信投顧</option>
                                <option value = "元富">元富</option>
                                <option value = "國票">國票</option>
                                <option value = "元大">元大</option>
                                <option value = "統一投顧">統一投顧</option>
                                <option value = "第一金">第一金</option>
                                <option value = "富邦台灣">富邦台灣</option>
                                <option value = "SinoPac">永豐金證券</option>
                            </select>
                        </div>
                    </div>

                    <div className = 'form-group row py-3'>
                        <label htmlFor = "upload" className = "col-md-2 col-form-label text-center">檔案上傳:</label>
                        <div className = 'col-md-3'>
                            <input type = "file" id = "upload" onChange = { saveFile }/>
                        </div>
                    </div>

                    <div className = 'form-group row py-3'>
                        <label htmlFor = "evaluate" className = "col-md-2 col-form-label text-center">評價:</label>
                        <div className = 'col-md-3'>
                            <select id = "evaluate" className = "form-select" onChange = {e => setEvaluate(e.target.value)}>
                                <option value = "買進">買進</option>
                                <option value = "強力買進">強力買進</option>
                                <option value = "賣出">賣出</option>
                                <option value = "中立">中立</option>
                                <option value = "未評等">未評等</option>
                            </select>
                        </div>
                    </div>

                    <div className = 'form-group row py-3'>
                        <div className = 'col-md-12 text-center'>
                            <button type = "submit" className = "btn btn-primary" style = {{ width : "100px" }}>送出</button>
                        </div>
                    </div>
                </form>
            </div>
        </>
    );
}

export default SelfUploadPage;
