import { Backdrop, CircularProgress } from '@mui/material';
import React, { useState } from 'react';

function NewsComp() {
    var Today = new Date();
    const [startDate, setStartDate] = useState(Today)
    const [loading, setLoading] = useState(false)
    const [column, setColumn] = useState("title")
    const [content, setContent] = useState("")

    const submit = (e) => {
        e.preventDefault()
    }

    return (
        <>
            <Backdrop
                sx = {{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                open = { loading }
            >
                資料載入中&emsp;
                <CircularProgress color = "inherit" />
            </Backdrop>

            <div className = 'row mx-auto py-3' style = {{ width : "50vw" }}>
                <h3 className = "display-6 text-center">新聞資料庫查詢</h3>

                <form onSubmit = { submit }>
                    <div className = 'form-group row py-3'>
                        <label htmlFor = "columns" className = "col-md-3 col-form-label text-center">搜尋欄位:</label>
                        <div className = 'col-md-3'>
                            <select id = "columns" className = "form-select" onChange = {e =>
                                setColumn(e.target.value)}>
                                <option value = "title">標題</option>
                                <option value = "category">新聞類別</option>
                                <option value = "repoter">記者</option>
                            </select>
                        </div>

                        <div className = 'col-md-6'>
                            <input type = "text" className = "form-control" onChange = { e => 
                                setContent(e.target.value) }></input>
                        </div>
                    </div>

                    <div className = 'form-group row py-3'>
                        <label htmlFor = "date" className = "col-md-3 col-form-label">新聞資料起始日:</label>
                        <div className = 'col-md-3'>
                            <input type = "date" id = "date" className = "form-control" onChange = {e => 
                                setStartDate(e.target.value)} value = { startDate }></input>
                        </div>
                    </div>

                    <div className = 'form-group row py-3'>
                        <div className = 'col-md-12 text-center'>
                            <button type = "submit" className = "btn btn-primary" style = {{ width : "100px" }}>搜尋</button>
                        </div>
                    </div>
                </form>
            </div>
        </>
    );
}

export default NewsComp;
