export function formatExplain(ma, maType) {
    return(
        {
            "method1" : {
                "explain0": "方法一",
                "explain1" : `天花板線 = 正乖離率由小到大排序後的第95%當作門檻值 * ${ma} ${maType} + ${ma} ${maType}`,
                "explain2" : `地板線 = 負乖離率由小到大排序後的第5%當作門檻值 * ${ma} ${maType} + ${ma} ${maType}`
            },
            "method2" : {
                "explain0": "方法二",
                "explain1" : `月均線 - 月均線 * (乖離率 - 2倍標準差)`
            },
            "method3" : {
                "explain0": "方法三",
                "explain1" : "天花板線 = 正乖離率由小到大排序後的第95%當作門檻值 * MA + MA",
                "explain2" : "地板線 = 負乖離率由小到大排序後的第5%當作門檻值 * MA + MA"
            },
        }
    )
}
