import React, { useState } from 'react';
import TechnicalAnalyzeComp from './technicalAnalyzeComp';
import Tabs from "@mui/material/Tabs";
import Tab from '@mui/material/Tab';
import CurrentConditionComp from './currentConditionComp';

function AnalysisComp() {
    const [type, setType] = useState(0)
    const [conditions, setConditions] = useState([])

    const handleConditionsChange = (newItems) => {
        if((!props.conditions.includes(e.target.innerText)) && (props.conditions.length <= 3)){
            props.conditions.push(e.target.innerText)
            props.handleConditionsChange(props.conditions)
        }
        setConditions(newItems)
    }

    const handleChange = (event, newValue) => {
        setType(newValue);
    };

    return (
        <>
            <div className = 'col-md-6'>
                <div className = 'card py-3 px-4'>
                    <Tabs value = { type } onChange = { handleChange } variant = "scrollable" scrollButtons = "auto">
                        <Tab label = "基本面"></Tab>
                        <Tab label = "技術面"></Tab>
                    </Tabs>

                    <div className = 'card-body'>
                        { type === 0 && <div>基本面</div> }
                        { type === 1 && <TechnicalAnalyzeComp conditions = { conditions } handleConditionsChange = { handleConditionsChange }/> }
                    </div>
                </div>
            </div>

            <div className = 'col-md-6'>
                <div className = 'card py-3 px-4'>
                    <div className = 'card-header text-center'>
                        目前篩選條件
                    </div>

                    <div className = 'card-body'>
                        <CurrentConditionComp conditions = { conditions } />
                    </div>
                </div>
            </div>
        </>
    );
}

export default AnalysisComp;
