import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Pagination } from 'react-bootstrap';
// import CustomListComp from './customListComp';

function UserListComp() {
    const [users, setUsers] = useState([]);
    const [page, setPage] = useState(1);
    const [count, setCount] = useState(1);
    const [renderData, setRenderData] = useState(users.slice(0, 8));

    useEffect(() => {
        axios.get("http://140.116.214.154:3000/api/data/userList")
        .then((res) => {
            console.log(res.data.length)
            setUsers(res.data)
            setRenderData(res.data.slice(0, 8))
            setCount(res.data.length / 8)
        }).catch((res) => {

        })
    }, [])

    const handleChange = (event, value) => {
        setPage(value);
        setRenderData(users.slice(8 * (value - 1), 8 * value));
    };

    return (
        <>
            <div className = 'text-center mt-2'>
                <h1 className = "display-5">成員列表</h1>
            </div>

            <div className = 'container mx-auto mt-5 text-center' style = {{ width : "90%" }}>
                <div className = 'row'>
                    { renderData.slice(0 , 4).map((d, i) => (
                        <div key = { i } className = 'col' style = {{ borderStyle : 'solid', borderWidth : "1px", margin : "5px" }}>{ d.name }</div>
                    )) }
                </div>

                <div className = 'row'>
                    { renderData.slice(4 , 8).map((d, i) => (
                        <div key = { i } className = 'col'  style = {{ borderStyle : 'solid', borderWidth : "1px", margin : "5px" }}>{ d.name }</div>
                    )) }
                </div>

                <Pagination
                    count = { count }
                    page = { page }
                    onChange = { handleChange }
                />
            </div>
        </>
    );
}

export default UserListComp;
