import React, { useState } from 'react';
import { columns_popular_ticker } from '../column/column';
import { StripedDataGrid } from '../stripedDataGrid/stripedDataGrid';

function PopularTicker() {
    const [pageSize, setPageSize] = useState(5)
    const [data, setData] = useState([])

    useEffect(() => {
        axios.get(config["rootApiIP"] + "/data/newestNews20")
        .then(res => {
            setNews(res.data)
        }).catch(res => {
            if(res.response.data === "Session expired") window.location.reload()
        })

        axios.get(config["rootApiIP"] + "/data/newestResearch20")
        .then(res => {
            setResearch(res.data)
        }).catch(res => {
            if(res.response.data === "Session expired") window.location.reload()
        })
    }, [])

    return (
        <>
            <div className = 'row mx-auto'>
                <div className = 'col-md-8 mx-auto py-3'>
                    <h3 className = "display-6 text-center">一週內熱門個股Top5</h3>

                    <StripedDataGrid
                        columns = { columns_popular_ticker }
                        rows = { data }
                        pageSize = { pageSize }
                        onPageSizeChange = { (newPageSize) => setPageSize(newPageSize) }
                        rowsPerPageOptions = {[5]}
                        getRowId = { row => row.ID }
                        pagination
                        autoHeight
                        disableColumnMenu
                        disableColumnSelector
                        disableDensitySelector
                        disableColumnFilter
                        disableSelectionOnClick = { true }
                        getRowClassName={(params) =>
                            params.indexRelativeToCurrentPage % 2 === 0 ? 'even' : 'odd'
                        }
                    />
                </div>
            </div>
        </>
    );
}

export default PopularTicker;
