import React, { useEffect, useState } from 'react';

function NavbarNotifyComp(props) {
    const [read, setRead] = useState(false)
    const [notifyList, setNotifyList] = useState([])

    function handleUnread(e){
        e.preventDefault()
        if(read === true){
            setRead(false)

            //update notifyList
        }
    }

    function handleRead(e){
        e.preventDefault()
        if(read === true){
            setRead(false)

            //update notifyList
        }

    }

    useEffect(() => {
        let temp = []

        for(let i = 0; i < 5; i++){
            temp.push(
                <div className = 'card-body' key = { i }>
                    <h3 className = 'card-title'>2330</h3>
                    <h6 className = "card-subtitle text-muted">穿越地板線</h6>
                </div>
            )

            if(i === 4){
                setNotifyList(temp)
            }
        };
    }, [])

    return (
        <>
            <div className = " container" style = {{ width : "200px" }}>
                <div className = 'row'>
                    <div className = 'col text-center' style = {{ cursor : "pointer" , borderRight: "1px solid #ADADAD"}} onClick = { () => setRead(false) }>
                        未讀
                    </div>

                    <div className = 'col text-center' style = {{ cursor : "pointer" }} onClick = { () => setRead(true) }>
                        已讀
                    </div>
                </div>

                <hr />

                <div className = 'row' id = "NotifyList">
                    {notifyList}
                </div>
            </div>
        </>
    );
}

export default NavbarNotifyComp;
