import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Typeahead } from 'react-bootstrap-typeahead';
import { rootApiIP } from '../../constant'

function StockPricingStratagyComp() {
    const [stockNum, setStockNum] = useState([]);
    const [autocom, setAutocom] = useState([]);
    const [loading, setLoading] = useState(false);
    const [inputError, setInputError] = useState(false);

    useEffect(() => {
        axios.get(rootApiIP + "/data/autoCom")
        .then(res => {
            setAutocom(res.data);
        }).catch(res => {
            if(res.response.data === "Session expired") window.location.reload()
        })
    }, [])

    function submit(e){
        e.preventDefault();

        if(stockNum !== ""){
            // const formData = new FormData();
            // formData.append("title", title);
            // formData.append("selectFile", file);
            // formData.append("filename", fileName);

            // axios.post(rootApiIP + "/data/upload/industry_analysis_upload", formData, {
            //     headers : { "Content-Type": "multipart/form-data" }
            // }).then(res => {
            //     alert("上傳成功")
            // }).catch(res => {
            //     if(res.response.data === "Session expired") window.location.reload()
            // })
        }else{
            alert("請選擇檔案")
        }
    }

    return (
        <>
            <div className = 'row text-center mx-auto mt-3'>
                <h3>會議紀錄上傳</h3>

                <div className = 'mt-3'>
                    <label htmlFor = "upload">檔案上傳:&emsp;</label>
                    <input type = "file" id = "upload" />
                    <button className = "btn btn-primary" >送出</button>
                </div>
            </div>
            <div className = 'row mx-auto py-3' style = {{ width : "40vw" }}>
                <h3 className = "display-6 text-center">股票定價</h3>

                <div className = 'mt-3'>
                    <label htmlFor = "upload">檔案上傳:&emsp;</label>
                    <input type = "file" id = "upload" />
                    <button className = "btn btn-primary" >送出</button>
                </div>

                <form className = 'form-inline' onSubmit = { submit }>
                    <div className = 'form-group'>
                        <label htmlFor = "stockNum_or_Name">股票代號&名稱:&emsp;</label>

                        <Typeahead

                            id = "stockNum_or_Name"
                            labelKey = "stock_num_name"
                            onChange = { setStockNum }
                            options = { autocom }
                            placeholder = "請輸入股票代號或名稱"
                            selected = { stockNum }
                        />
                    </div>

                    <div className = 'form-group pt-4 text-center'>
                        { inputError ? <p style = {{ color : "red" }}>股票代號&名稱格式錯誤</p> : <></> }
                        {loading && <button type = "submit" className = "btn btn-primary" style = {{ width : "200px" }} disabled><span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span></button>}
                        {!loading && <button type = "submit" className = "btn btn-primary" style = {{ width : "200px" }}>搜尋</button>}
                    </div>
                </form>
            </div>
        </>
    );
}

export default StockPricingStratagyComp;
