import { Alert, Snackbar } from '@mui/material';
import React, { useState } from 'react';

function SubButtonComp() {
    const [open, setOpen] = useState(false);
    const [message, setMessage] = useState("")

    function handleClick(e){
        console.log(e.target)
        setMessage(e.target.value)
        setOpen(true)
    };

    const handleClose = (event, reason) => {
      if (reason === 'clickaway') {
        return;
      }

      setOpen(false);
    };

    return (
        <>
            <div className = "dropdown">
                <button className = "btn btn-danger dropdown-toggle" type = "button" data-bs-toggle = "dropdown" aria-expanded = "false">
                    訂閱
                </button>

                <ul className = "dropdown-menu dropdown-menu-end">
                    <li><button className = "dropdown-item" type = "button" onClick = { (e) => handleClick(e) }>網站通知</button></li>
                    <li><button className = "dropdown-item" type = "button">Email通知</button></li>
                    <li><button className = "dropdown-item" type = "button">Line通知</button></li>
                </ul>
            </div>

            <Snackbar open = {open} autoHideDuration = { 5000 } onClose = { handleClose }>
                <Alert onClose = { handleClose } severity = "success" sx = {{ width: '100%' }}>
                    {message} 訂閱成功
                </Alert>
            </Snackbar>
        </>
    );
}

export default SubButtonComp;
