import React, { useEffect, useState } from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';

function NavbarNotifyComp(props) {
    const [allRead, setAllRead] = useState(false)
    const [notifyList, setNotifyList] = useState([])
    const [init, setInit] = useState(false)

    function handleAll(){
        if(allRead === true){
            setAllRead(false)

            //update notifyList
            let temp1 = []

            for(let i = 0; i < 5; i++){
                temp1.push(
                    <div className = 'card h-100' key = { i }>
                        <div className = 'card-body'>
                            <h3 className = 'card-title'>2330</h3>
                            <h6 className = "card-subtitle text-muted">穿越地板線</h6>
                            <h6 className = "card-subtitle text-muted">2022/10/0{i + 1}</h6>
                        </div>
                    </div>
                )

                if(i === 4){
                    setNotifyList(temp1)
                }
            };
        }
    }

    function handleRead(){
        if(allRead === false){
            setAllRead(true)
            //update notifyList
            let temp2 = []

            for(let i = 0; i < 5; i++){
                temp2.push(
                    <div className = 'card h-100' key = { i }>
                        <div className = 'card-body'>
                            <h3 className = 'card-title'>2330</h3>
                            <h6 className = "card-subtitle text-muted">穿越地板線</h6>
                            <h6 className = "card-subtitle text-muted">2022/09/0{i + 1}</h6>
                        </div>
                    </div>
                )

                if(i === 4){
                    setNotifyList(temp2)
                }
            };
        }
    }

    useEffect(() => {
        if(init === false){
            setInit(true)

            let temp = []

            for(let i = 0; i < 5; i++){
                temp.push(
                    <div className = 'card h-100' key = { i }>
                        <div className = 'card-body'>
                            <h3 className = 'card-title'>2330</h3>
                            <h6 className = "card-subtitle text-muted">穿越地板線</h6>
                            <h6 className = "card-subtitle text-muted">2022/10/0{i + 1}</h6>
                        </div>
                    </div>
                )

                if(i === 4){
                    setNotifyList(temp)
                }
            };
        }else{
            handleRead()
            handleAll()
        }
    }, [])

    return (
        <>
            <div className = " container" style = {{ width : "15vw" }}>
                <div className = 'row' style = {{ borderBottom : "1px solid #ADADAD" }}>
                    { !allRead ? 
                        <div className = 'col text-center' style = {{ cursor : "pointer" , borderRight: "1px solid #ADADAD", backgroundColor : "gray"}} onClick = { () => handleAll() }>
                            全部
                        </div> :

                        <div className = 'col text-center' style = {{ cursor : "pointer" , borderRight: "1px solid #ADADAD"}} onClick = { () => handleAll() }>
                            全部
                        </div>
                    }

                    { allRead ? 
                        <div className = 'col text-center' style = {{ cursor : "pointer" , borderRight: "1px solid #ADADAD", backgroundColor : "gray"}} onClick = { () => handleRead() }>
                            已讀
                        </div> :

                        <div className = 'col text-center' style = {{ cursor : "pointer" , borderRight: "1px solid #ADADAD"}} onClick = { () => handleRead() }>
                            已讀
                        </div>
                    }
                </div>

                <div className = 'row' id = "NotifyList"  style = {{ width : "15vw" }}>
                    <List
                        sx = {{
                            width: '100%',
                            bgcolor: 'background.paper',
                            position: 'relative',
                            overflow: 'auto',
                            maxHeight: 300,
                        }}
                    >
                        {notifyList}
                    </List>
                </div>
            </div>
        </>
    );
}

export default NavbarNotifyComp;
