import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';

function ChooseTickerDetail() {
    const scale = {
        0 : [{ "height" : "100%" }],
        1 : [{ "height" : "80%" }, { "top" : "80%", "height" : "20%" }],
        2 : [{ "height" : "70%" }, { "top" : "70%", "height" : "15%" }, { "top" : "85%", "height" : "15%"  }],
        3 : [{ "height" : "60%" }, { "top" : "60%", "height" : "13%" }, { "top" : "73%", "height" : "13%"  }, { "top" : "86%", "height" : "14%" }]
    }

    const [searchParams, setSearchParams] = useSearchParams();
    const [scaleType, setScaleType] = useState(0)
    const [stockNum, setStockNum] = useState("")
    const [conditions, setConditions] = useState([])

    const [options, setOptions] = useState({
        yAxis: [],
        xAxis: {
            type : "datetime",
        },
        chart : {
            height : 600
        },
        accessibility: {
            enabled : false
        },
        series: [],
        stockTools : {
            gui : {
                buttons : [ 'indicators', 'separator', 'simpleShapes', 'lines', 'crookedLines', 'measure', 'advanced',
                'toggleAnnotations', 'separator', 'verticalLabels', 'flags', 'separator', 'zoomChange', 'fullScreen',
                'typeChange', 'separator', 'currentPriceIndicator', 'saveChart' ]
            }
        },
        tooltip: {
            valueDecimals: 2
        },
        rangeSelector : {
            selected: 0
        },
        plotOptions : {
            series : {
                dataGrouping : {
                    enabled : false
                }
            },
            candlestick: {
                color: 'green',
                upColor: 'red'
            }
        }
    });

    useEffect(() => {
        searchParams.forEach((value, key) => {
            if(key === "stock_num"){
                setStockNum(value)
            }else{
                if(value === true){
                    conditions.push(key)

                    setConditions([...conditions])
                    setScaleType(scaleType + 1)

                    setOptions({
                        yAxis : scale[scaleType + 1]
                    })
                    console.log(conditions)
                }
            }
        });
    }, [])

    return (
        <>
        </>
    );
}

export default ChooseTickerDetail;
