import axios from 'axios';
import { CSVLink } from "react-csv";
import React, { useEffect, useState } from 'react';
import { DataGrid } from '@mui/x-data-grid';

function DatabaseComp() {
    let [data, setData] = useState([]);

    const columns = [
        { field: "dbName", headerName : "資料庫名稱", flex: 1, headerAlign: 'center', align: 'center', hide : 'true' },
        { field: 'dataQuantity', headerName: '資料總筆數', flex: 1, headerAlign: 'center', align: 'center' },
        { field: 'newestDate', headerName: '最新資料日期', flex: 1, headerAlign: 'center', align: 'center' },
        { field: 'allData', headerName: '資料總表下載', flex: 1, headerAlign: 'center', sortable: false, align: 'center', renderCell : rowData => <CSVLink data = { rowData.value } filename = { rowData.dbName + ".csv" }>Download me</CSVLink> },
    ];

    useEffect(() => {
        axios.get("http://140.116.214.154:3000/api/data/allData")
        .then(res => {
            setData(res.data);
        }).catch(res => {
        })
    }, [])

    return (
        <>
            <div className = 'row mx-auto py-3' style = {{ width : "40vw" }}>
                <div className = 'col-md-10 mx-auto py-3'>
                    <h3 className = "display-6 text-center">資料庫總表</h3>

                    <DataGrid
                        columns = { columns } 
                        rows = { data }
                        getRowId = { row => row.dbName }
                        autoHeight
                        disableColumnMenu
                        disableColumnSelector
                        disableDensitySelector
                        disableColumnFilter
                    />
                </div>
            </div>
        </>
    );
}

export default DatabaseComp;
