import React, { useState } from 'react';
import TickerSearchComp from '../../tickerSearchComp';
import { AutoCom } from '../../../autoCom';
import { investment_company } from '../../../constant';
import { Backdrop, CircularProgress } from '@mui/material';

function FinancialDataComp() {
    var date = new Date();
    const today = date.getFullYear() + "-" + String(date.getMonth() + 1).padStart(2, '0') + "-" + String(date.getDate()).padStart(2, '0')
    date.setDate(1);
    date.setMonth(date.getMonth() - 3);

    const last3Month = date.getFullYear() + "-" + String(date.getMonth() + 1).padStart(2, '0') + "-" + String(date.getDate()).padStart(2, '0')
    const autocom = AutoCom.AutoComList;

    const [ticker, setTicker] = useState("")
    const [loading, setLoading] = useState(false)
    const [startDate, setStartDate] = useState(last3Month)
    const [endData, setEndDate] = useState(today)
    const [investmentCompany, setInvestmentCompany] = useState(investment_company[0])
    const [tickerError, setTickerError] = useState(false)

    const submit = (e) => {

    }

    return (
        <>
            <Backdrop
                sx = {{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                open = { loading }
            >
                資料載入中&emsp;
                <CircularProgress color = "inherit" />
            </Backdrop>

            <div className = 'row mx-auto'>
                <form onSubmit = { submit }>
                    <div className = 'form-group row py-3'>
                        <label htmlFor = "stockNum_or_Name" className = "col-md-2 col-form-label text-center">股票代號&名稱:</label>
                        <div className = 'col-md-3'>
                            <TickerSearchComp init = "" setTicker = {setTicker}/>
                        </div>
                    </div>

                    <div className = 'form-group row py-3'>
                        <label htmlFor = "startDate" className = "col-md-1 col-form-label text-center">開始日期:</label>
                        <div className = 'col-md-3'>
                            <input type = "date" id = "startDate" className = "form-control"
                                onChange = {e => setStartDate(e.target.value)} value = { startDate }></input>
                        </div>

                        <label htmlFor = "endDate" className = "col-md-1 col-form-label text-center">到</label>
                        <div className = 'col-md-3'>
                            <input type = "date" id = "endDate" className = "form-control"
                                onChange = {e => setEndDate(e.target.value)} value = { endData }></input>
                        </div>
                    </div>

                    <div className = 'form-group row py-3'>
                        <label htmlFor = "provider" className = "col-md-1 col-form-label text-center">券商名稱:</label>
                        <div className = 'col-md-3'>
                            <select id = "provider" className = "form-select" onChange = {e => investmentCompany(e.target.value)}>
                                {
                                    investment_company.map(function(ele, idx){
                                        return <option key = {idx} value = {ele}>{ele}</option>
                                    })
                                }
                            </select>
                        </div>
                    </div>

                    <div className = 'form-group py-3 text-center'>
                        { tickerError ? <p style = {{ color : "red" }}>格式錯誤(必須符合自動完成格式 股票代號 名稱)</p> : <></> }
                        <button id = 'submit' type = "submit" className = "btn btn-primary" 
                            style = {{ width : "200px" }}>搜尋</button>
                    </div>
                </form>
            </div>
        </>
    );
}

export default FinancialDataComp;
