import React, { useState } from 'react';
import { columns_home_news } from '../column/column';
import { StripedDataGrid } from '../stripedDataGrid/stripedDataGrid';
import { GridToolbar } from '@mui/x-data-grid';

function NewsItem(props) {
    const [pageSizeNews, setPageSizeNews] = useState(25)
    const [pageSizeResearch, setPageSizeResearch] = useState(25)
    const table = [
        { "page" : pageSizeNews, "setPage" : setPageSizeNews },
        { "page" : pageSizeResearch, "setPage" : setPageSizeResearch },
        { "page" : pageSizeCalendar, "setPage" : setPageSizeCalendar }
    ]
    return (
        <>
            <div className = 'row'>
                {
                    table.forEach((ele, idx) => {
                        <div className = 'col-md-4'>
                            <StripedDataGrid
                                columns = { columns_home_news }
                                rows = { props.data[idx] }
                                pageSize = { pageSize }
                                onPageSizeChange={ (newPageSize) => setPageSize(newPageSize) }
                                rowsPerPageOptions = {[5, 10, 25]}
                                getRowId = { row => row.ID }
                                components = {{ Toolbar: GridToolbar }}
                                componentsProps = {{ toolbar: { showQuickFilter: true },}}
                                pagination
                                autoHeight
                                disableColumnMenu
                                disableColumnSelector
                                disableDensitySelector
                                disableColumnFilter
                                disableSelectionOnClick = { true }
                                getRowClassName={(params) =>
                                    params.indexRelativeToCurrentPage % 2 === 0 ? 'even' : 'odd'
                                }
                            />
                        </div>
                    })
                }
            </div>
        </>
    );
}

export default NewsItem;
